INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (3, 'Démolition', 4, 1, false, 1, null, null, null, null, null, 1, 'A1', 'Démolition d’ouvrage par des moyens manuels ou mécaniques (hors explosifs). Cette activité comprend, pour les
raccordements et la protection des existants, les travaux accessoires ou complémentaires de :
• Maçonnerie.
• Zinguerie, couverture et étanchéité.
• VRD.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (4, 'Terrassement', 8, null, false, 1, null, null, null, null, null, 0, 'A2', 'Réalisation à ciel ouvert, de creusement et de blindage de fouilles provisoires dans les sols, ainsi que des travaux de
rabattement de nappes nécessaires à l’exécution des travaux, de remblai, d’enrochement non lié et de comblement sauf
pour les carrières ayant pour objet soit de constituer par eux-mêmes un ouvrage soit de permettre la réalisation d’un
ouvrage. Cette activité comprend les sondages et forages.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (5, 'Amélioration des sols', 3, null, false, 1, null, null, null, null, null, 0, 'A3', 'Traitement, renforcement et comportement des sols en place et en vue d’en améliorer les caractéristiques physiques et
mécaniques. Cette activité comprend la pose de géotextile (hors membranes), les sondages et forages.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (6, 'Sondages et Forages', 6, 1, false, 1, null, null, null, null, null, 0, 'A3.1', 'Cette activité comprend la pose de géotextile (hors membranes), les sondages et forages, dans la limite de 50 mètres.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (7, 'VRD Canalisations Assainissement Chaussées Trotoirs Pavage Arrosage Espaces verts', 8, null, false, 1, null, null, null, null, null, 0, 'A4', 'Réalisation de canalisations, d’assainissement autonome, de réseaux enterrés, de voiries piétonnes et carrossables, de
poteaux et clôtures. Réalisation d’espaces verts, y compris les travaux complémentaires de maçonnerie.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (8, 'Paysagiste', 10, null, false, 1, null, null, null, null, null, 1, 'A4.1', 'Réalisation d’espaces verts, y compris systèmes d’arrosages, de poteaux et clôtures.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (9, 'Montage d''échafaudages Etaiement', 6, 1, false, 1, null, null, null, null, null, 0, 'A5', 'Montage pour le compte de tiers de tous étaiements, échafaudages fixes, suspendus ou élévateurs employés
à la construction ou à l’entretien des immeubles, monuments et édifices ainsi que le montage de structures
évènementielles.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (11, 'Traitement curatifs Insectes xylophages Champignons', 5, 1, false, 1, null, null, null, null, null, 0, 'A7', 'Traitement curatif des bois en œuvre et des constructions contre les insectes à larves xylophages, les termites et les
champignons dans les charpentes et menuiseries bois, mais aussi les sols, fondations, murs, cloisons et planchers.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (13, 'Fondations spéciales', 3, null, false, 2, null, null, null, null, null, 0, 'A9', 'Réalisation de systèmes de fondations profondes tels que pieux, barrettes,
parois moulées, palplanches, parois de soutènement autonomes et toutes techniques équivalentes. Cette activité com-
prend les rabattements de nappes, les sondages et forages.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (16, 'Taille de pierre', 8, null, false, 2, null, null, null, null, null, 0, 'A10.2', 'Taille et façonnage de pierres de construction et de menus ouvrages en pierre.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (17, 'Béton précontraint', 3, null, false, 2, null, null, null, null, null, 0, 'A11', 'Mise en œuvre de béton armé précontraint mis en tension sur chantier.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (23, 'Etancheité et imperméabilisation de cuvelage, réservoirs et piscines', 3, null, false, 3, null, null, null, null, null, 0, 'A16', 'Réalisation de travaux d’étanchéité et d’imperméabilisation de cuvelage d’ouvrages en sous-pression hydrostatique, de
réservoirs et piscines en béton armé et béton précontraint. Cette activité comprend les travaux préparatoires, comme
ceux de protection et de traitement de tous les joints.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (26, 'Agencement cuisines, magasins, salles de bain', 9, null, false, 3, null, null, null, null, null, 0, 'A18.1', 'Réalisation de tout travaux d''installation de cuisines, magasins, salles de bains. Cette activité regroupe les activités
suivantes :
22. Menuiseries intérieures.
23. Plâtrerie-Staff-Stuc-Gypserie.
27. Revêtements de surface en matériaux souples et parquets flottants y compris revêtements muraux en matériaux durs
30. Plomberie-Installations sanitaires.
Ainsi que les travaux accessoires ou complémentaires correspondant aux activités :
26. Peinture.
34. Electricité.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (32, 'Plâtrerie - Staff - Stuc - Gypserie', 10, null, false, 4, null, null, null, null, null, 0, 'A23', 'Réalisation de plâtrerie, cloisonnement et faux plafonds à base de plâtre. Cette activité comprend les travaux
accessoires ou complémentaires de :
• menuiseries intégrées aux cloisons,
• Le doublage thermique ou acoustique intérieur,
• mise en œuvre des matériaux ou produits contribuant à l’isolation thermique, acoustique et à la sécurité incendie.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (36, 'Nettoyage (Intérieur)', 10, null, false, 4, null, null, null, null, null, 0, '26.1', 'Nettoyage de locaux exercé à titre accessoire de l’activité de peinture.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (37, 'Nettoyage (Extérieur)', 10, null, false, 4, null, null, null, null, null, 0, 'A26.2', 'Nettoyage haute pressions de sols, façades et toitures (démoussage).');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (41, 'Isolation thermique et acoustique', 10, null, false, 4, null, null, null, null, null, 0, 'A29', 'Réalisation, y compris leurs revêtements et menuiseries de :
• isolation thermique de murs, parois, sols, plafonds et toitures de tous ouvrages,
• isolation et de traitement acoustique par la mise en œuvre de matières ou matériaux adaptés,
• calorifugeage des circuits, tuyauteries et appareils.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (43, 'Isolation frigorifique', 8, null, false, 4, null, null, null, null, null, 0, 'A29.2', 'Réalisation de travaux d’isolation frigorifique des locaux d’une capacité maximale de 100m 3 , et fonctionnant à toutes
températures.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (48, 'Fumisterie', 8, null, false, 5, null, null, null, null, null, 0, 'A32', 'Réalisation (hors fours et cheminée industriels) de systèmes d’évacuation des produits de combustion. Cette activité
comprend les travaux de :
• construction et installations d’âtres et foyers, construction de socles de chaudières,
• pose d’insert, poêle à bois, poêle à granulés,
• pose sur le sol de carreaux réfractaires et céramiques.
Ainsi que les travaux accessoires ou complémentaires de :
• raccords d’enduits divers,
• pose de tubage,
• calorifugeage des conduits,
• revêtements en carreaux et panneaux de faïence.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (53, 'Fours et cheminées Industriels', 3, null, false, 5, null, null, null, null, null, 0, 'A35', 'Réalisation d’ouvrages de construction de fours industriels, d’incinération et de crémation y compris leurs cheminées.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (54, 'Ascenseurs', 3, null, false, 5, null, null, null, null, null, 0, 'A36', 'Réalisation d’ascenseurs, monte-charge, monte-personne, escalier mécanique et trottoir roulant, y compris les organes
et équipements nécessaires à leur fonctionnement.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (55, 'Piscines', 3, null, false, 5, null, null, null, null, null, 0, 'A37', 'Réalisation de piscines y compris les organes et équipements nécessaires à leur utilisation hors technique de
géothermie et pose de capteurs solaires.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (59, 'Installations d''éoliennes domestiques', 7, 1, false, 5, null, null, null, null, null, 0, 'A41', 'Réalisation d’éoliennes domestiques d’une puissance inférieur à 20kwh et d’une hauteur inférieur à 12 mètres.
Cette activité comprend les travaux de :
• tranchées, fondation du mat et des haubans,
• réalisation de socles ancrages et supports des mats et des haubans,
• raccordements électrique du matériel,
• zinguerie d’éléments d’accessoires.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (10, 'Traitement amiante', 3, 1, false, 1, null, null, null, null, null, 0, 'A6', 'Retrait, encapsulage ou encoffrement de l’amiante, des matériaux, d’équipements et produits en contenant, ainsi que
leur élimination ou leur confinement, dans tout ouvrage ou partie d’ouvrage. Cette activité nécessite :
• Une Qualification Amiante (AFNOR ou QUALI-PV).
• Un processus de contrôle.
• Un agrément de transport pour les matières dangereuses si transport.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (12, 'Assèchement des murs', 5, 1, false, 1, null, null, null, null, null, 0, 'A8', 'Traitement des murs contre les remontées d’humidité par capillarité. Cette activité comprend les travaux préparatoires et
de traitement proprement dits et les travaux accessoires et complémentaires de remplacement des parements.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (14, 'Maçonnerie et béton armé sauf précontraint in situ', 8, null, false, 2, null, null, null, null, null, 1, 'A10', 'Réalisation de maçonnerie en béton armé préfabriqué ou non, en béton précontraint préfabriqué (hors précontrainte in
situ), en blocs agglomérés de mortier ou de béton cellulaire, en pierre naturelles ou briques, ceci tant en infrastructure
qu’en superstructure, par toutes les techniques de maçonneries de coulage, hourdage (hors revêtement mural agrafé,
attaché ou collé). Cette activité comprend les travaux de :
• enduits à base de liants hydrauliques ou de synthèse,
• ravalement en maçonnerie,
• de briquetage, pavage,
• dallage, chape,
• fondations autres que pieux, barrettes, parois moulés, palplanches, parois de soutènement autonomes et toutes au-
tres techniques équivalentes.
Ainsi que les travaux accessoires ou complémentaires de :
• terrassement et de canalisations enterrées,
• complément d’étanchéité des murs enterrés,
• pose de matériaux contribuant à l’isolation intérieure,
• la pose de renforts bois ou métal nécessités par l’ouverture de baies et les reprises en sous-œuvre,
• démolition et VRD,
• pose d’huisseries,
• pose d’éléments simples de charpente, ne comportant ni entaille, ni assemblage, et scellés directement à la
maçonnerie, et à l’exclusion de toute charpente préfabriquée dans l’industrie,
• plâtrerie,
• carrelage, faïence et revêtement en matériaux durs à base minérale,
• calfeutrement de joints.
Et les travaux suivants liés à la fumisterie :
• construction, réparation et entretien d’âtres et foyers (hors four et cheminée industriels),
• conduits de fumées et de ventilation à usage domestique et individuel,
• ravalement et réfection des souches hors combles,
• construction de cheminées à usage domestique et individuel,
• revêtements en carreaux et panneaux de faïence.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (15, 'Enduits Hydrauliques', 9, null, false, 2, null, null, null, null, null, 1, 'A10.1', 'Réalisation d’enduits à base de liants hydrauliques ou de synthèse, ravalement en maçonnerie. Ainsi que les travaux
accessoires ou complémentaires de : complément d’étanchéité des murs enterrés, plâtrerie.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (18, 'Charpente et structure en bois', 8, null, false, 2, null, null, null, null, null, 0, 'A12', 'Réalisation de charpentes, structures et ossatures à base de bois à l’exclusion des façades-rideaux. Cette activité
comprend les travaux accessoires ou complémentaires de :
• couverture, bardage, châssis divers, lorsque ceux-ci sont fixés directement à l’ossature,
• supports de couverture ou d’étanchéité,
• plafonds, faux plafonds, cloisons en bois et autres matériaux, planchers et parquets,
• isolation thermique et acoustique liées à l’ossature et la charpente,
• traitement préventif et curatif des bois,
• mise en œuvre matériaux ou de tous éléments métalliques concourant à l’édification, au renforcement ou à la
stabilité des charpentes et escaliers.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (19, 'Charpente et structure métallique', 8, null, false, 2, null, null, null, null, null, 0, 'A13', 'Réalisation de charpentes, structures et ossatures métalliques à l’exclusion des façades-rideaux. Cette activité
comprend les travaux accessoires ou complémentaires de :
• couverture, bardage, châssis divers, lorsque ceux-ci sont métalliques et fixés directement à l’ossature,
• supports de couverture ou d’étanchéité,
• protection et traitement contre la corrosion,
• traitement pour la stabilité au feu par peinture ou flocage,
• travaux en sous œuvre par structure métallique,
• isolation thermique et acoustique liées à l’ossature ou à la charpente.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (20, 'Couverture (y compris travaux d''étanchéité dans la limite de 150 m2 par chantier)', 8, null, false, 3, null, null, null, null, null, 0, 'A14', 'Réalisation en tout matériaux (hors structures textiles), y compris par bardeau bitumé, de couverture, vêtage, vêture.
Cette activité comprend les travaux de :
• zinguerie et éléments accessoires en PVC,
• pose de châssis de toit (y compris exutoires en toiture), de capteurs solaires,
• réalisation d’isolation et d’écran sous toiture,
• ravalement réfection des souches hors combles,
• installation de paratonnerre.
Ainsi que les travaux accessoires ou complémentaires de :
• raccords d’étanchéité,
• réalisation de bardages verticaux.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (21, 'Ravalement', 9, null, false, 3, null, null, null, null, null, 0, 'A14.1', 'Réalisation d’enduits À base de liants hydrauliques ou de synthèse, ravalement en maçonnerie, la réalisation de
peinture, y compris les revêtements plastiques épais ou semi-épais (RPE et RSE), de ravalement en peinture ou par
nettoyage, de pose de revêtements souples, textiles, plastiques ou assimilés sur surfaces horizontales et verticales.
Cette activité comprend les travaux accessoires ou complémentaires de :
• complément d’étanchéité des murs enterrés,
• plâtrerie,
• menuiserie,
• revêtement de faïence,
• nettoyage, sablage, grenaillage,
• isolation acoustique et thermique par l’intérieur et l’extérieur.
Ne sont pas compris les travaux d’imperméabilisation et d’étanchéité.');
INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (22, 'Etanchéité de toiture, terrasse et plancher intérieur', 4, 1, false, 3, null, null, null, null, null, 0, 'A15', 'Réalisation d’étanchéité de toiture, terrasse et plancher intérieur par mise en œuvre de matériaux bitumineux ou de
synthèse sur des supports horizontaux ou inclinés, y compris la pose du support d’étanchéité. Cette activité comprend
dans la limite éventuelle fixée au procédé, la mise en œuvre de matériaux d’isolation et inclut tous travaux préparant
l’application ou assurant la protection du revêtement étanche, ainsi que ceux complétant l’étanchéité des ouvrages.
Ainsi que la réalisation des travaux de :
