INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (29, 'Façade - Rideaux', 9, null, false, 3, null, null, null, null, null, 0, 'A20', 'Réalisation des façades-rideaux quel que soit le matériaux utilisé, y compris la mise en place des éléments de
