INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (38, 'Revêtements de surfaces en matériaux souple et parquets flottants', 10, null, false, 4, null, null, null, null, null, 0, 'A27', 'Réalisation de parquets collés ou flottants, de revêtements souples, avec ou sans support textile, en tout matériaux
