INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (40, 'Revêtements de surfaces à base de résines, ycompris sols sportif et résines de sols industriel (Limité à 500m2)', 6, 1, false, 4, null, null, null, null, null, 0, 'A28.1', 'Réalisation de chapes, revêtements de murs et sols à base de liant synthétique ou résine, y compris sols sportifs et
