INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (42, 'Isolation thermique et acoustique par l''extérieur', 9, null, false, 4, null, null, null, null, null, 0, 'A29.1', 'Réalisation de travaux d’isolation thermique et acoustique par l’extérieur de tout bâtiment neuf ou ancien par procédé
