INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (44, 'Plomberie - Installation Sanintaire - Chauffage', 10, null, false, 5, null, null, null, null, null, 0, 'A30', 'Réalisation d’installations (production, distribution, évacuation) sanitaires et d’eau chaude (sanitaire et de chauffage),
