INSERT INTO public.ta_activite (id_activite, libl_activite, id_classe_ta_classe, id_t_franchise_ta_t_franchise, actif, id_famille_activite_ta_famille_activite, qui_cree, quand_cree, qui_modif, quand_modif, ip_acces, version_obj, code_activite, description_activite) VALUES (56, 'Maisons à ossature bois', 3, null, false, 5, null, null, null, null, null, 0, 'A38', 'Réalisation de l’ensemble des éléments en bois ou dérivés de bois. Cette activité comprend les travaux accessoires ou
complémentaires de :
• plafonds, faux plafonds, cloisons en bois et autres matériaux,
• menuiserie intérieure et extérieure,
• isolation thermique et acoustique,
• traitement préventif et curatif des bois,
• traitement des bois en œuvre contre les insectes xylophages,
